import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { registerUser, getAdminConfig } from './api';
import AboutMeComponent from './components/form-components/AboutMeComponent';
import AddressComponent from './components/form-components/AddressComponent';
import BirthdateComponent from './components/form-components/BirthdateComponent';
import AdminDashboard from './components/AdminDashboard';
import DataTable from './components/DataTable';
import './App.css';

// Main Onboarding Component
function OnboardingWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [userData, setUserData] = useState({});
  const [adminConfig, setAdminConfig] = useState({ 2: [], 3: [] });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadAdminConfig();
  }, []);

  const loadAdminConfig = async () => {
    try {
      const config = await getAdminConfig();
      setAdminConfig(config);
    } catch (error) {
      console.error('Failed to load admin config:', error);
    }
  };

  const handleStep1Submit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    // Store email and password in userData state (not database yet)
    setUserData(prev => ({
      ...prev,
      email: formData.get('email'),
      password: formData.get('password')
    }));

    // Move to step 2
    setCurrentStep(2);
    alert('âœ… Account info captured! Moving to Step 2.');
  };

  const handleDataChange = (field, value) => {
    setUserData(prev => ({ ...prev, [field]: value }));
  };

  const handleNextStep = () => {
    if (currentStep === 2) {
      setCurrentStep(3);
      alert('âœ… Step 2 completed! Moving to Step 3.');
    } else if (currentStep === 3) {
      // Only NOW save to database when completing all steps
      handleCompleteRegistration();
    }
  };

  const handleCompleteRegistration = async () => {
    setLoading(true);
    setError('');

    try {
      // Create complete user data object
      const completeUserData = {
        email: userData.email,
        password: userData.password,
        aboutMe: userData.aboutMe || '',
        streetAddress: userData.streetAddress || '',
        city: userData.city || '',
        state: userData.state || '',
        zip: userData.zip || '',
        birthdate: userData.birthdate || null
      };

      // Save to database only now
      const response = await registerUser(completeUserData);

      alert(`ðŸŽ‰ Registration Complete! User saved to database with ID: ${response.id}. Check the data table to see your info.`);

      // Reset form
      setCurrentStep(1);
      setUserData({});
    } catch (error) {
      setError('Registration failed. Email might already exist.');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  const renderComponents = (pageNumber) => {
    const components = adminConfig[pageNumber] || [];

    return components.map(componentType => {
      switch (componentType) {
        case 'ABOUT_ME':
          return (
            <AboutMeComponent
              key="about-me"
              value={userData.aboutMe}
              onChange={handleDataChange}
            />
          );
        case 'ADDRESS':
          return (
            <AddressComponent
              key="address"
              values={userData}
              onChange={handleDataChange}
            />
          );
        case 'BIRTHDATE':
          return (
            <BirthdateComponent
              key="birthdate"
              value={userData.birthdate}
              onChange={handleDataChange}
            />
          );
        default:
          return null;
      }
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸŽ¯ Zealthy Onboarding</h1>

        {/* Progress Bar */}
        <div className="progress-bar" style={{ 
          display: 'flex', 
          gap: '20px', 
          marginBottom: '30px' 
        }}>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 1 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>1</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 2 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>2</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 3 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>3</div>
        </div>

        {/* Show current progress */}
        <div style={{
          backgroundColor: '#e3f2fd',
          padding: '10px 20px',
          borderRadius: '5px',
          marginBottom: '20px',
          color: '#1976d2'
        }}>
          <strong>Current Step:</strong> {currentStep} | 
          <strong> Data Status:</strong> {Object.keys(userData).length > 0 ? 'In Progress' : 'Not Started'}
        </div>

        {/* Error Message */}
        {error && (
          <div style={{ 
            color: '#dc3545', 
            backgroundColor: '#f8d7da', 
            padding: '10px', 
            borderRadius: '5px', 
            marginBottom: '20px' 
          }}>
            {error}
          </div>
        )}

        {/* Step 1: Email/Password */}
        {currentStep === 1 && (
          <div className="step-content">
            <h2>Step 1: Create Account</h2>
            <p style={{ color: '#666' }}>Enter your email and password. Data will be saved only when you complete all steps.</p>

            <form onSubmit={handleStep1Submit} style={{ maxWidth: '400px', margin: '0 auto' }}>
              <div style={{ marginBottom: '15px' }}>
                <input 
                  type="email" 
                  name="email"
                  placeholder="Email" 
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <input 
                  type="password" 
                  name="password"
                  placeholder="Password (min 6 characters)" 
                  minLength="6"
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>

              <button 
                type="submit"
                style={{ 
                  width: '100%', 
                  padding: '12px', 
                  backgroundColor: '#007bff',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: 'pointer'
                }}
              >
                Next Step â†’
              </button>
            </form>
          </div>
        )}

        {/* Step 2: Dynamic Components */}
        {currentStep === 2 && (
          <div className="step-content">
            <h2>Step 2: Tell Us About Yourself</h2>
            <p style={{ color: '#666' }}>Fill out the information below. Nothing is saved to database yet.</p>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(2)}
            </div>

            {adminConfig[2]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            <button 
              onClick={handleNextStep}
              style={{ 
                padding: '12px 30px', 
                backgroundColor: '#28a745', 
                color: 'white', 
                border: 'none', 
                borderRadius: '5px',
                fontSize: '16px',
                cursor: 'pointer',
                marginTop: '20px'
              }}
            >
              Next Step â†’
            </button>
          </div>
        )}

        {/* Step 3: Dynamic Components */}
        {currentStep === 3 && (
          <div className="step-content">
            <h2>Step 3: Almost Done!</h2>
            <p style={{ color: '#666' }}>Complete the final step. Clicking "Complete Registration" will save all your data to the database.</p>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(3)}
            </div>

            {adminConfig[3]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            {/* Show current data preview */}
            <div style={{ 
              backgroundColor: '#f8f9fa', 
              padding: '15px', 
              borderRadius: '5px', 
              margin: '20px auto',
              maxWidth: '600px'
            }}>
              <h4>Data Preview (will be saved to database):</h4>
              <ul style={{ textAlign: 'left' }}>
                <li><strong>Email:</strong> {userData.email}</li>
                {userData.aboutMe && <li><strong>About Me:</strong> {userData.aboutMe.substring(0, 50)}...</li>}
                {userData.streetAddress && <li><strong>Address:</strong> {userData.streetAddress}</li>}
                {userData.birthdate && <li><strong>Birthdate:</strong> {userData.birthdate}</li>}
              </ul>
            </div>

            <button 
              onClick={handleNextStep}
              disabled={loading}
              style={{ 
                padding: '12px 30px', 
                backgroundColor: loading ? '#ccc' : '#dc3545', 
                color: 'white', 
                border: 'none', 
                borderRadius: '5px',
                fontSize: '16px',
                cursor: loading ? 'not-allowed' : 'pointer',
                marginTop: '20px'
              }}
            >
              {loading ? 'Saving to Database...' : 'Complete Registration ðŸŽ‰'}
            </button>
          </div>
        )}

        {/* Navigation */}
        <div style={{ marginTop: '40px' }}>
          <nav>
            <Link to="/admin" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ”§ Admin Dashboard
            </Link>
            {' | '}
            <Link to="/data" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ“Š View Data
            </Link>
          </nav>
        </div>
      </header>
    </div>
  );
}

