import React from 'react';
import { getFieldDisplayName } from '../../utils/fieldNames';
import './FormComponents.css';


const ADDRESS_FIELDS = [
  { key: 'streetAddress', fullWidth: true },
  { key: 'city', maxLength: null },
  { key: 'state', maxLength: 2 },
  { key: 'zip', maxLength: null }
];

function AddressComponent({ values, onChange, errors = {}, required = false }) {
  const renderField = (fieldConfig) => {
    const { key, maxLength, fullWidth } = fieldConfig;
    const displayName = getFieldDisplayName(key);
    const error = errors[key];
    const value = values?.[key] || '';

    return (
      <div key={key} className="field-container">
        <input
          type="text"
          value={value}
          onChange={(e) => onChange(key, e.target.value)}
          placeholder={required ? `${displayName} (Required)` : displayName}
          maxLength={maxLength}
          required={required}
          className={`form-input ${error ? 'error' : ''}`}
        />
        {error && (
          <div className={`error-message ${fullWidth ? '' : 'small'}`}>
            {error}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="form-component">
      <h3>
        üè† {getFieldDisplayName('address')} Information {required && <span className="required-asterisk">*</span>}
      </h3>
      <div className="address-grid">
        {/* Street Address - Full Width */}
        {renderField(ADDRESS_FIELDS[0])}
        
        {/* City, State, ZIP - Row */}
        <div className="address-row">
          {ADDRESS_FIELDS.slice(1).map(renderField)}
        </div>
      </div>
    </div>
  );
}

export default AddressComponent;
