import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { getAdminConfig } from './api';
import AboutMeComponent from './components/form-components/AboutMeComponent';
import AddressComponent from './components/form-components/AddressComponent';
import BirthdateComponent from './components/form-components/BirthdateComponent';
import AdminDashboard from './components/AdminDashboard';
import DataTable from './components/DataTable';
import './App.css';

// Main Onboarding Component
function OnboardingWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [userData, setUserData] = useState({});
  const [adminConfig, setAdminConfig] = useState({ 2: [], 3: [] });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadAdminConfig();
    restoreUserProgress();
  }, []);

  // Save user data to localStorage whenever it changes
  useEffect(() => {
    if (Object.keys(userData).length > 0) {
      localStorage.setItem('zealthy_user_data', JSON.stringify(userData));
      console.log('Saved userData to localStorage:', userData);
    }
  }, [userData]);

  // Save current step to localStorage whenever it changes
 useEffect(() => {
  if (currentStep > 1) {  // Only save if beyond step 1
    localStorage.setItem('zealthy_current_step', currentStep.toString());
    console.log('Saved currentStep to localStorage:', currentStep);
  }
}, [currentStep]);

  const loadAdminConfig = async () => {
    try {
      const config = await getAdminConfig();
      setAdminConfig(config);
    } catch (error) {
      console.error('Failed to load admin config:', error);
    }
  };

  const restoreUserProgress = () => {
    console.log('Attempting to restore user progress...');

    // Restore user data from localStorage
    const savedUserData = localStorage.getItem('zealthy_user_data');
    if (savedUserData) {
      try {
        const parsedData = JSON.parse(savedUserData);
        setUserData(parsedData);
        console.log('Restored user data:', parsedData);
      } catch (error) {
        console.error('Failed to parse saved user data:', error);
        localStorage.removeItem('zealthy_user_data');
      }
    }

    // Restore current step from localStorage
    const savedStep = localStorage.getItem('zealthy_current_step');
    if (savedStep) {
      const stepNumber = parseInt(savedStep);
      if (stepNumber >= 1 && stepNumber <= 3) {
        setCurrentStep(stepNumber);
        console.log('Restored to step:', stepNumber);
      }
    }
  };

  // Check if email exists in database
  const checkEmailExists = async (email) => {
    try {
      const response = await fetch(`http://localhost:8080/api/users/email/${encodeURIComponent(email)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      // If response is 200, email exists
      // If response is 404, email doesn't exist
      return response.ok;
    } catch (error) {
      console.error('Error checking email:', error);
      return false;
    }
  };

  const handleStep1Submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const formData = new FormData(e.target);
    const email = formData.get('email');
    const password = formData.get('password');

    try {
      // Check if email already exists in database
      const emailExists = await checkEmailExists(email);

      if (emailExists) {
        setError('This email is already registered. Please use a different email.');
        setLoading(false);
        return;
      }

      // Email is unique, proceed to step 2
      const newUserData = {
        ...userData,
        email: email,
        password: password
      };

      setUserData(newUserData);
      setCurrentStep(2);
      alert('âœ… Email validated! Moving to Step 2.');

    } catch (error) {
      setError('Failed to validate email. Please try again.');
      console.error('Email validation error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDataChange = (field, value) => {
    setUserData(prev => ({ ...prev, [field]: value }));
  };

  const handleNextStep = () => {
    if (currentStep === 2) {
      setCurrentStep(3);
      alert('âœ… Moving to Step 3.');
    } else if (currentStep === 3) {
      handleCompleteRegistration();
    }
  };

  const handleCompleteRegistration = async () => {
    setLoading(true);
    setError('');

    try {
      const completeUserData = {
        email: userData.email,
        password: userData.password,
        aboutMe: userData.aboutMe || null,
        streetAddress: userData.streetAddress || null,
        city: userData.city || null,
        state: userData.state || null,
        zip: userData.zip || null,
        birthdate: userData.birthdate || null
      };

      console.log('Submitting complete user data:', completeUserData);

      const response = await fetch('http://localhost:8080/api/users/register-complete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(completeUserData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Registration failed: ${errorText}`);
      }

      const result = await response.json();

      alert(`ðŸŽ‰ Registration Complete! User ID: ${result.id}. Check the data table to see your info.`);

      // Clear all saved data after successful completion
      localStorage.removeItem('zealthy_user_data');
      localStorage.removeItem('zealthy_current_step');

      // Reset form
      setCurrentStep(1);
      setUserData({});
    } catch (error) {
      setError('Registration failed. Please try again.');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  const clearProgress = () => {
    if (window.confirm('Are you sure you want to start over? All progress will be lost.')) {
      localStorage.removeItem('zealthy_user_data');
      localStorage.removeItem('zealthy_current_step');
      setCurrentStep(1);
      setUserData({});
      setError('');
    }
  };

  const renderComponents = (pageNumber) => {
    const components = adminConfig[pageNumber] || [];

    return components.map(componentType => {
      switch (componentType) {
        case 'ABOUT_ME':
          return (
            <AboutMeComponent
              key="about-me"
              value={userData.aboutMe || ''}
              onChange={handleDataChange}
            />
          );
        case 'ADDRESS':
          return (
            <AddressComponent
              key="address"
              values={userData}
              onChange={handleDataChange}
            />
          );
        case 'BIRTHDATE':
          return (
            <BirthdateComponent
              key="birthdate"
              value={userData.birthdate || ''}
              onChange={handleDataChange}
            />
          );
        default:
          return null;
      }
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸŽ¯ Zealthy Onboarding</h1>

        {/* Progress Bar */}
        <div className="progress-bar" style={{ 
          display: 'flex', 
          gap: '20px', 
          marginBottom: '30px' 
        }}>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 1 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>1</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 2 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>2</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 3 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>3</div>
        </div>

        {/* Progress Status */}
        {currentStep > 1 && Object.keys(userData).length > 0 && (
          <div style={{
            backgroundColor: '#e3f2fd',
            padding: '10px 20px',
            borderRadius: '5px',
            marginBottom: '20px',
            color: '#1976d2',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <span>
              <strong>Progress Saved!</strong> Step {currentStep} | Email: {userData.email}
            </span>
            <button 
              onClick={clearProgress}
              style={{
                padding: '5px 10px',
                backgroundColor: '#dc3545',
                color: 'white',
                border: 'none',
                borderRadius: '3px',
                fontSize: '12px',
                cursor: 'pointer'
              }}
            >
              Start Over
            </button>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div style={{ 
            color: '#dc3545', 
            backgroundColor: '#f8d7da', 
            padding: '10px', 
            borderRadius: '5px', 
            marginBottom: '20px',
            border: '1px solid #f5c6cb'
          }}>
            <strong>Error:</strong> {error}
          </div>
        )}

        {/* Step 1: Email/Password */}
        {currentStep === 1 && (
          <div className="step-content">
            <h2>Step 1: Create Account</h2>
            <p style={{ color: '#666', marginBottom: '20px' }}>
              Enter your email and password. We'll check if the email is already registered.
            </p>

            <form onSubmit={handleStep1Submit} style={{ maxWidth: '400px', margin: '0 auto' }}>
              <div style={{ marginBottom: '15px' }}>
                <input 
                  type="email" 
                  name="email"
                  defaultValue={userData.email || ''}
                  placeholder="Email" 
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <input 
                  type="password" 
                  name="password"
                  defaultValue={userData.password || ''}
                  placeholder="Password (min 6 characters)" 
                  minLength="6"
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>

              <button 
                type="submit"
                disabled={loading}
                style={{ 
                  width: '100%', 
                  padding: '12px', 
                  backgroundColor: loading ? '#6c757d' : '#007bff',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Validating Email...' : 'Next Step â†’'}
              </button>
            </form>
          </div>
        )}

        {/* Step 2: Dynamic Components */}
        {currentStep === 2 && (
          <div className="step-content">
            <h2>Step 2: Tell Us About Yourself</h2>
            <p style={{ color: '#666', marginBottom: '20px' }}>
              Your progress is automatically saved. You can refresh or come back later.
            </p>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(2)}
            </div>

            {adminConfig[2]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            <button 
              onClick={handleNextStep}
              style={{ 
                padding: '12px 30px', 
                backgroundColor: '#28a745', 
                color: 'white', 
                border: 'none', 
                borderRadius: '5px',
                fontSize: '16px',
                cursor: 'pointer',
                marginTop: '20px'
              }}
            >
              Next Step â†’
            </button>
          </div>
        )}

        {/* Step 3: Dynamic Components */}
        {currentStep === 3 && (
          <div className="step-content">
            <h2>Step 3: Almost Done!</h2>
            <p style={{ color: '#666', marginBottom: '20px' }}>
              Complete the final step to save your registration to the database.
            </p>

            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '30px', 
              maxWidth: '600px', 
              margin: '20px auto' 
            }}>
              {renderComponents(3)}
            </div>

            {adminConfig[3]?.length === 0 && (
              <p style={{ color: '#666' }}>No components configured for this step.</p>
            )}

            {/* Data Preview */}
            <div style={{ 
              backgroundColor: '#f8f9fa', 
              padding: '15px', 
              borderRadius: '5px', 
              margin: '20px auto',
              maxWidth: '600px',
              border: '1px solid #dee2e6'
            }}>
              <h4 style={{ marginTop: 0 }}>Review Your Information:</h4>
              <div style={{ textAlign: 'left' }}>
                <p><strong>Email:</strong> {userData.email}</p>
                {userData.aboutMe && <p><strong>About Me:</strong> {userData.aboutMe.length > 50 ? userData.aboutMe.substring(0, 50) + '...' : userData.aboutMe}</p>}
                {userData.streetAddress && <p><strong>Address:</strong> {userData.streetAddress}, {userData.city} {userData.state} {userData.zip}</p>}
                {userData.birthdate && <p><strong>Birthdate:</strong> {userData.birthdate}</p>}
              </div>
            </div>

            <button 
              onClick={handleNextStep}
              disabled={loading}
              style={{ 
                padding: '12px 30px', 
                backgroundColor: loading ? '#6c757d' : '#dc3545', 
                color: 'white', 
                border: 'none', 
                borderRadius: '5px',
                fontSize: '16px',
                cursor: loading ? 'not-allowed' : 'pointer',
                marginTop: '20px'
              }}
            >
              {loading ? 'Saving to Database...' : 'Complete Registration ðŸŽ‰'}
            </button>
          </div>
        )}

        {/* Navigation */}
        <div style={{ marginTop: '40px' }}>
          <nav>
            <Link to="/admin" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ”§ Admin Dashboard
            </Link>
            {' | '}
            <Link to="/data" style={{ color: '#007bff', textDecoration: 'none' }}>
              ðŸ“Š View Data
            </Link>
          </nav>
        </div>
      </header>
    </div>
  );
}

// Main App with Router
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<OnboardingWizard />} />
        <Route path="/admin" element={<AdminDashboard />} />
        <Route path="/data" element={<DataTable />} />
      </Routes>
    </Router>
  );
}

export default App;
