import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { registerUser, updateUserStep, getAdminConfig } from './api';
import AboutMeComponent from './components/form-components/AboutMeComponent';
import AddressComponent from './components/form-components/AddressComponent';
import BirthdateComponent from './components/form-components/BirthdateComponent';
import AdminDashboard from './components/AdminDashboard';
import DataTable from './components/DataTable';
import './App.css';

// Main Onboarding Component
function OnboardingWizard() {
  const [currentStep, setCurrentStep] = useState(1);
  const [userId, setUserId] = useState(null);
  const [userData, setUserData] = useState({});
  const [adminConfig, setAdminConfig] = useState({ 2: [], 3: [] });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadAdminConfig();
    checkExistingUser();
  }, []);

  const loadAdminConfig = async () => {
    try {
      const config = await getAdminConfig();
      setAdminConfig(config);
    } catch (error) {
      console.error('Failed to load admin config:', error);
    }
  };

  const checkExistingUser = () => {
    const savedUserId = localStorage.getItem('zealthy_user_id');
    const savedStep = localStorage.getItem('zealthy_current_step');
    if (savedUserId && savedStep) {
      setUserId(parseInt(savedUserId));
      setCurrentStep(parseInt(savedStep));
    }
  };

  const handleStep1Submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const formData = new FormData(e.target);
    const newUserData = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await registerUser(newUserData);
      setUserId(response.id);
      setCurrentStep(2);

      // Save progress to localStorage
      localStorage.setItem('zealthy_user_id', response.id);
      localStorage.setItem('zealthy_current_step', '2');

      alert(`âœ… Registration successful! User ID: ${response.id}`);
    } catch (error) {
      setError('Registration failed. Email might already exist.');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDataChange = (field, value) => {
    setUserData(prev => ({ ...prev, [field]: value }));
  };

  const handleNextStep = async () => {
    if (currentStep === 2) {
      // Save step 2 data and move to step 3
      try {
        await updateUserStep(userId, 2, userData);
        setCurrentStep(3);
        localStorage.setItem('zealthy_current_step', '3');
      } catch (error) {
        alert('Failed to save data');
        console.error('Update error:', error);
      }
    } else if (currentStep === 3) {
      // Save step 3 data and complete
      try {
        await updateUserStep(userId, 3, userData);
        alert('ðŸŽ‰ Registration Complete! Check the data table to see your info.');
        localStorage.removeItem('zealthy_user_id');
        localStorage.removeItem('zealthy_current_step');
        setCurrentStep(1);
        setUserId(null);
        setUserData({});
      } catch (error) {
        alert('Failed to complete registration');
        console.error('Complete error:', error);
      }
    }
  };

  const renderComponents = (pageNumber) => {
    const components = adminConfig[pageNumber] || [];

    return components.map(componentType => {
      switch (componentType) {
        case 'ABOUT_ME':
          return (
            <AboutMeComponent
              key="about-me"
              value={userData.aboutMe}
              onChange={handleDataChange}
            />
          );
        case 'ADDRESS':
          return (
            <AddressComponent
              key="address"
              values={userData}
              onChange={handleDataChange}
            />
          );
        case 'BIRTHDATE':
          return (
            <BirthdateComponent
              key="birthdate"
              value={userData.birthdate}
              onChange={handleDataChange}
            />
          );
        default:
          return null;
      }
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸŽ¯ Zealthy Onboarding</h1>

        {/* Progress Bar */}
        <div className="progress-bar" style={{ 
          display: 'flex', 
          gap: '20px', 
          marginBottom: '30px' 
        }}>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 1 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>1</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 2 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>2</div>
          <div style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%', 
            backgroundColor: currentStep >= 3 ? '#007bff' : '#ccc',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: 'bold'
          }}>3</div>
        </div>

        {/* Error Message */}
        {error && (
          <div style={{ 
            color: '#dc3545', 
            backgroundColor: '#f8d7da', 
            padding: '10px', 
            borderRadius: '5px', 
            marginBottom: '20px' 
          }}>
            {error}
          </div>
        )}

        {/* Step 1: Email/Password */}
        {currentStep === 1 && (
          <div className="step-content">
            <h2>Step 1: Create Account</h2>
            <form onSubmit={handleStep1Submit} style={{ maxWidth: '400px', margin: '0 auto' }}>
              <div style={{ marginBottom: '15px' }}>
                <input 
                  type="email" 
                  name="email"
                  placeholder="Email" 
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>
              <div style={{ marginBottom: '20px' }}>
                <input 
                  type="password" 
                  name="password"
                  placeholder="Password (min 6 characters)" 
                  minLength="6"
                  required 
                  style={{ 
                    width: '100%', 
                    padding: '12px', 
                    borderRadius: '5px', 
                    border: '1px solid #ccc',
                    fontSize: '16px'
                  }}
                />
              </div>
              <button 
                type="submit" 
                disabled={loading}
                style={{ 
                  width: '100%', 
                  padding: '12px', 
                  backgroundColor: loading ? '#ccc' : '#007bff',
                  color: 'white', 
                  border: 'none', 
                  borderRadius: '5px',
                  fontSize: '16px',
                  cursor: loading ? 'not-allowed' : 'pointer'
                }}
              >
                {loading ? 'Creating Account...' : 'Next Step â†’'}
              </button>
            </form>
          </div>
        )}

        {/* Step 2: Dynamic Components */}
        {currentStep === 2 && (
          <div className="step-content">
            <h2>Step 2: Tell Us About Yourself</h2>
